import React from 'react';

class Title extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			counter: 0
		}
		this.onClick = this.onClick.bind(this);
	}

	onClick(){
		this.setState({counter : this.state.counter + 1 });
	}

	render(){
		return (
			<h1>
				<span>{this.state.counter}</span>
				{this.props.children}
			</h1>
		)
	}
}

export default Title;
